import React, { useState, useEffect } from "react";
import axios from "axios";
import "./index.less";
import Navinput from "../component/navinput/index";
export default function height() {
  const [data, setData] = useState(null);
  useEffect(() => {
    const query = window.location.pathname.substring(8);
    console.log(query);
    const getData = async () => {
      const res = await axios.get(`https://api.fsn365.com/blocks/${query}`);
      setData(res.data.data);
      // console.log(res.data.data);
    };
    getData();
  }, []);
  console.log(data);
  return (
    <div>
      <Navinput />
      <div className="blockscontainer">
        <h2 className="blockstext">Blocks</h2>
        <div className="bl-box">
          {data && (
            <div className="blockson">
              <div className="blockson-box ">
                <p>Height:{data.height}</p>
                <hr size="1"></hr>
              </div>
              <div className="blockson-box ">
                <p>Age:{data.age}</p>
                <hr size="1"></hr>
              </div>
              <div className="blockson-box ">
                <p>Transactions:</p>
                <hr size="1"></hr>
              </div>
              <div className="blockson-box ">
                <p>Block Miner:</p>
                <hr size="1"></hr>
              </div>
              <div className="blockson-box ">
                <p>Block Rewards:{data.reward}</p>
                <hr size="1"></hr>
              </div>
              <div className="blockson-box ">
                <p>Difficulty:{data.difficulty}</p>
                <hr size="1"></hr>
              </div>
              <div className="blockson-box ">
                <p>Size:{data.size}</p>
                <hr size="1"></hr>
              </div>
              <div className="blockson-box ">
                <p>Gas Limit:{data.gasLimit}</p>
                <hr size="1"></hr>
              </div>
              <div className="blockson-box ">
                <p>Parent Hash:{data.hash}</p>
                <hr size="1"></hr>
              </div>
              <div className="blockson-box ">
                <p> Hash:{data.hash}</p>
                <hr size="1"></hr>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
